theme_fields.value:
.topic-list > tbody > tr:nth-child(odd) {
    background-color: white;
}


.topic-list > tbody tr:not(.last-visit):not(.topic-list-item-separator) td {
    border-bottom: 1px solid #eee;
}

.topic-list a.title:not(.badge-notification) {
    color: #3b5998;
    font-weight: normal;
    font-family: 'Helvetica Neue', Helvetica, Arial, Utkal, sans-serif;
    font-size: 18px;
}

.topic-list a.title:not(.badge-notification):hover {
    text-decoration: underline;
}

.topic-list .creator, .topic-list .editor {
    font-size: 13px;
    display: block;
    margin-top: 3px;
}

.topic-list .main-link {
    width: auto;
}

.topic-list {
    td, .creator a, .editor a {
        color: #888;
    }
}

.topic-list td {
    padding-bottom: 10px;
}

.topic-list th {
    color: black;
    background-color: #fafafa;
}
.topic-list {
    border-top: none;
}

.topic-list td:first-of-type {
    padding-bottom: 10px;
    padding-left: 10px;
    padding-top: 10px;
}

.topic-list tr.topic-list-item-separator td:first-of-type {
    padding: 0;
}


.topic-list {
    .fa-tag {
        opacity: 0.7;
        font-size: 10px;
        margin-right: -2px;
    }
    .num.posts-map {
        position: relative;
        font-size: 17px;
        a {
            color: #888 !important;
            font-weight: normal;
        }
        a.heatmap-high {
            color: #c66 !important;   
        }
        width: 100px;
    }
}

.topic-list td.last-post, .topic-list th.activity {
    width: 160px;
    .relative-date {
        color: #666;
    }
}

.topic-list .creator .relative-date {
    margin-left: 8px;
}

.badge-category-bg {
    opacity: 0.8;
}

.topic-list .badge-wrapper.bar {
    .badge-category {
      color: #9a9a9a !important;
      font-weight: 600;
      font-size: 12px;
      padding: 2px 6px;
    }
    .badge-category-bg {
        top: -1px;
    }
}

.last-post {
    .poster-avatar {
        margin-right: 10px;
    }
    .poster-avatar, .poster-info {
        float: left;
    }
}

.topic-list .posts {
    width: auto;
}

/* topic page */
#topic-progress {
    background-color: #f1f1f1;
    color: #666;
    font-weight: normal;
    .bg {
       background-color: #bee9e9;
    }
}

nav.post-controls button.has-like {
    color: #08c;
}

.title-wrapper .discourse-tag {
    border-radius: 5px !important;
    border: 0px solid #D0D0D0 !important;
    padding: 1px 6px !important;
    color: #1D1D1D !important;
    background-color: #F1F1F1 !important;
}

.badge-wrapper.bullet span.badge-category {
    color: #333 !important;
}

.creator .badge-wrapper.bullet span.badge-category {
    color: #888 !important;
    margin-top: -2px;
}

img.avatar {
    border-radius: 3px;
}

#suggested-topics td.main-link {
    width: 500px;
}

.topic-list a.title.visited:not(.badge-notification) {
    color: #6644aa;
}
